    fun useMostRecentKey() {
        val id = prefs.getString(PREF_KEY_ID, null)
        id ?: return;
        keystore = Keystore("pm.kee.vault.$id")
        decryptStorage()
    }
    fun setKey(id: String) {
        keystore = Keystore("pm.kee.vault.$id")
        with (prefs.edit()) {
            putString(PREF_KEY_ID, id)
            commit()
        }
        decryptStorage()
    }

